mapper.select:
select * from ( select rownum as RNUM, A.* from ( select r.*, p.userid from tbl_refund r join tbl_payment p on r.payment_no=p.payment_no
order by r.refund_no desc ) A ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

mapper.update:
update tbl_refund
set refund_state='Y' , refund_date=sysdate
where refund_no=#{refundNo}
